mapper.select:
SELECT a.rv_id AS rvId ,a.m_title AS mTitle ,a.start_dt AS startDt ,a.end_dt AS endDt ,a.dept ,a.rv_user AS rvUser ,a.rv_writer AS rvWriter ,a.m_id AS mId ,a.reg_dt AS regDt ,a.up_dt AS upDT ,b.kor_nm as rvUserNm ,c.kor_nm as rvWriterNm ,d.dept_nm as deptNm FROM reservation a left join emp b on a.rv_user = b.emp_id left join emp c on a.RV_WRITER = b.emp_id left join departments d on a.dept = d.dept_id
${item} LIKE #{searchKey}
ORDER BY a.reg_dt DESC
